const { resolve } = require('path')
const HtmlWebpackPlugin = require('html-webpack-plugin')

module.exports = {
    entry: './src/index.js',
    output: {
        filename: 'built.js',
        path: resolve(__dirname, 'build')

    },
    module: {
        rules: [{
            test: /\.less$/,
            use: [
                //   在use数组中执行顺序是从左往右，从下往上
                'stytle-loader',
                'css-loader',
                'less-loader'
            ]
        }, {
            test: /\.(png|gif|jpg)/,
            loader: 'url-loader',
            options: {
                limit: 8 * 1024
            }
        }]

    },
    plugins: [
        new HtmlWebpackPlugin({
            template: "./src/index.html"
        })
    ],



    mode: 'development'
}